import React, { useState } from "react";
import Navigation from "../Navigation/Navigation";
import LogonForm from "../LogonForm/LogonForm";
import AdminDashboard from "../AdminDashboard/Dashboard";
import Cart from "../Cart/Cart";
import "./HomePage.css"; // Import the CSS file for styling

const HomePage = (props) => {
  const {
    verified,
    setVerified,
    signOn,
    setSignOn,
    employee,
    setEmployee,
    displayCart,
    setDisplayCart,
  } = props;

  const [employeeLogin, setEmployeeLogin] = useState(false);

  const handleEmployeeLogin = () => {
    setEmployeeLogin(true);
  };

  return (
    <div className="HomePage">
      <Navigation
        verified={verified}
        setVerified={setVerified}
        signOn={signOn}
        setSignOn={setSignOn}
        employee={employee}
        setEmployee={setEmployee}
        displayCart={displayCart}
        setDisplayCart={setDisplayCart}
      />
      <LogonForm
        signOn={signOn}
        setSignOn={setSignOn}
        setVerified={setVerified}
        employee={employee}
        setEmployee={setEmployee}
      />
      <Cart cart={displayCart} />
      {employee === true ? (
        <AdminDashboard />
      ) : (
        <div className="carousel">
          {/* Your carousel content */}
        </div>
      )}
      <div className="employee-login-container">
        {employeeLogin ? (
          <div className="employee-login">
            <h4>Employee Login</h4>
            {/* Add your employee login form here */}
          </div>
        ) : (
          <button
            className="employee-login-button"
            onClick={handleEmployeeLogin}
          >
            Employee Login
          </button>
        )}
      </div>
      <div className="body-content">List of best sellers goes here</div>
    </div>
  );
};

export default HomePage;
