import Navigation from "../Navigation/Navigation";
import LogonForm from "../LogonForm/LogonForm";
import AdminDashboard from "../AdminDashboard/Dashboard";
import Cart from "../Cart/Cart";

const HomePage = (props) => {
  const {
    verified,
    setVerified,
    signOn,
    setSignOn,
    employee,
    setEmployee,
    displayCart,
    setDisplayCart,
  } = props;

  const handleEmployeeLogin = () => {
    setSignOn(true);
  };

  return (
    <div className="HomePage">
      <Navigation
        verified={verified}
        setVerified={setVerified}
        signOn={signOn}
        setSignOn={setSignOn}
        employee={employee}
        setEmployee={setEmployee}
        displayCart={displayCart}
        setDisplayCart={setDisplayCart}
      />
      <LogonForm
        signOn={signOn}
        setSignOn={setSignOn}
        setVerified={setVerified}
        employee={employee}
        setEmployee={setEmployee}
      />
      <Cart cart={displayCart} />
      {employee === true ? (
        <AdminDashboard />
      ) : (
        <div>
          <div className="carousel">
            {/* Scrolling new release carousel can go here */}
          </div>
          <div className="body-content">List of best sellers goes here</div>
          <div className="employee-login-container">
            <div className="employee-login">
              <button className="employee-login-button" onClick={handleEmployeeLogin}>
                Employee Login
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default HomePage;
